<template>
    <div>
        <nav>
            <input type="text" v-model="searchItem" v-on:keyup.enter="search(searchItem)" placeholder="Find a secID">
            <button type="button" v-on:click="clearSearch()" v-bind:disabled="searchItem === ''">Clear</button>
            <input type="checkbox" id="rtSimulate" v-model="rtSimulate">
            <label for="rtSimulate">show realtime simulation results</label>
        </nav>
        <div>
            <button type="button" v-on:click="selectPage(1)" v-bind:disabled="currentPage === 1 || totalPage === 0"><<</button>
            <span class="space"></span>
            <button type="button" v-on:click="selectPage(currentPage-1)" v-bind:disabled="currentPage === 1 || totalPage === 0"><</button>
            {{currentPage}} / {{totalPage}}
            <button type="button" v-on:click="selectPage(currentPage+1)" v-bind:disabled="currentPage === totalPage || totalPage === 0">></button>
            <span class="space"></span>
            <button type="button" v-on:click="selectPage(totalPage)" v-bind:disabled="currentPage === totalPage || totalPage === 0">>></button>
        </div>
        <div>
            <table>
                <tr>
                    <th><button v-on:click="sortBy('secID')" v-bind:style="{ color: (sortKey === 'secID' ? 'white' : 'black')}">SecID</button></th>
                    <th><button>Closed</button></th>
                    <th><button v-on:click="sortBy('st')" v-bind:style="{ color: (sortKey === 'st' ? 'white' : 'black')}">StartDate</button></th>
                    <th><button>StartPrice</button></th>
                    <th><button>LatestDate/EndDate</button></th>
                    <th><button>LatestPrice/EndPrice</button></th>
                    <th><button>TradePlan</button></th>
                </tr>
                <tr v-for="item in paginatedItems">
                    <td>{{item.secID}}</td>
                    <td>{{item.closed}}</td>
                    <td>{{item.st | formatTs}}</td>
                    <td>{{item.sp | numeral}}</td>
                    <td>{{item.et | formatTs}}</td>
                    <td>{{item.ep | numeral}}</td>
                    <td>{{item.tradeplanId}}</td>
                </tr>
            </table>
        </div>
        <button type="button" v-on:click="update()">Update</button>
    </div>
</template>

<script>
    module.exports =  {
        props: ['httpRequest'],
        data: function () {
            return {
                rtSimulate: false,
                searchItem: '',
                totalItems: 0,
                paginatedItems: [],
                sortKey: 'st',
                order: 1,
                pageSize: 10,
                currentPage: 1
            }
        },
        filters: {
            formatTs: function (dateTs) {
                return new Date(dateTs * 24 * 60 * 60 * 1000).toISOString().slice(0, 10);
            },
            numeral: function (num) {
                return num.toFixed(2);
            }
        },
        computed: {
            totalPage: function () {
                return Math.ceil(this.totalItems / this.pageSize);
            },
            sort: function () {
                var res = Object.create(null);
                res[this.sortKey] = this.order;
                return res;
            },
            path: function () {
                return this.rtSimulate ? '/rtSimulate' : '/simulate';
            },
            verb: function () {
                return 'getMul';
            }
        },
        methods: {
            data: function(page) {
                return {
                    pageNum: page,
                    pageSize: this.pageSize,
                    filter: this.searchItem === '' ? {} : { secID: this.searchItem.toLowerCase() },
                    sort: this.sort
                }
            },
            selectPage: function (page) {
                this.httpRequest(this.path, this.data(page), this.verb, (r) => {
                    var res = r instanceof Error ? {
                        pageNum: 0,
                        pageSize: this.pageSize,
                        total: 0,
                        data: []
                    } : JSON.parse(r);
                    this.currentPage = res.pageNum;
                    this.pageSize = res.pageSize;
                    this.totalItems = res.total;
                    this.paginatedItems = res.data;
                })
            },
            search: function () {
                this.selectPage(1);
            },
            clearSearch: function () {
                this.searchItem = '';
                this.selectPage(1);
            },
            update: function () {
                this.selectPage(this.currentPage);
            },
            sortBy: function (key) {
                if(key === this.sortKey) this.order = -this.order;
                else {
                    this.sortKey = key;
                    this.order = 1;
                }
                this.selectPage(this.currentPage);
            }
        },
        mounted: function () {
            this.selectPage(1);
        }
    }
</script>

<style>
    span.space {
        padding-left: 20px;
    }
</style>
