
const config = require('config');
const utility = require('utility')
const validate = utility.validate;
const azure = utility.azureStorage;
const path = require('path');

exports.checkPack = function (arg) {
    return validate.isStr(arg);
}

exports.run = function (secID) {
    var fileName = secID + '.json';
    return azure(config.azureUsr).getBlobToFile(config.stockdataContainer, fileName, path.join(config.stockDataDir, '/' + fileName));
}
