
/**
 * simulate collection: maintain all the simulate trades.
 * simdate collection: last simulate date for each tradeplan & secID
 */
const db = require('db');
const obj = {
    '_id': true,
    'tradeplanId': true,
    'secID': true,
    'st': true,
    'et': true,
    'ht': true,
    'lt': true,
    'sp': true,
    'ep': true,
    'hp': true,
    'lp': true,
    'closed': true
}

exports.rtSimulateCol = db.getCollection('rtSimulate', obj);

exports.simulateCol = db.getCollection('simulate', obj);

exports.simdateCol = db.getCollection('simdateCol', {
    '_id': true,
    'tradeplanId': true,
    'secID': true,
    'lastSimTs': true
})
